import { NativeModules } from 'react-native';

const { ImagePickerManager } = NativeModules;

const DEFAULT_OPTIONS = {
  title: 'Wähle ein Foto',
  cancelButtonTitle: 'Abbrechen',
  takePhotoButtonTitle: 'Foto schießen',
  chooseFromLibraryButtonTitle: 'Aus Galerie wählen',
  quality: 1.0,
  allowsEditing: false,
  permissionDenied: {
    title: 'Berechtigung verweigert',
    text:
      'Um ein Bild auszuwählen, wähle eines aus der Galerie.',
    reTryTitle: 'Wiederholen.',
    okTitle: "Ich bin sicher.",
  },
};

module.exports = {
  ...ImagePickerManager,
  showImagePicker: function showImagePicker(options, callback) {
    if (typeof options === 'function') {
      callback = options;
      options = {};
    }
    return ImagePickerManager.showImagePicker(
      { ...DEFAULT_OPTIONS, ...options },
      callback,
    );
  },
};
